import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Index from "./pages/Index";
import NotFound from "./pages/NotFound";

const queryClient = new QueryClient();

const App = () => (
  <QueryClientProvider client={queryClient}>

      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Index />} />
          {/* ADD ALL CUSTOM ROUTES ABOVE THE CATCH-ALL "*" ROUTE */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </BrowserRouter>
  </QueryClientProvider>
);

export default App;

input.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 222 47% 6%;
    --foreground: 210 40% 98%;

    --card: 222 47% 9%;
    --card-foreground: 210 40% 98%;

    --popover: 222 47% 9%;
    --popover-foreground: 210 40% 98%;

    --primary: 252 87% 67%;
    --primary-foreground: 222 47% 6%;

    --secondary: 217 91% 60%;
    --secondary-foreground: 210 40% 98%;

    --muted: 222 47% 14%;
    --muted-foreground: 215 20% 65%;

    --accent: 187 85% 53%;
    --accent-foreground: 222 47% 6%;

    --destructive: 0 62% 55%;
    --destructive-foreground: 210 40% 98%;

    --border: 222 47% 16%;
    --input: 222 47% 14%;
    --ring: 252 87% 67%;

    --radius: 0.75rem;

    /* Custom tokens */
    --sleep-excellent: 152 76% 53%;
    --sleep-good: 187 85% 53%;
    --sleep-fair: 43 96% 56%;
    --sleep-poor: 0 62% 55%;

    --gradient-primary: linear-gradient(135deg, hsl(252 87% 67%) 0%, hsl(217 91% 60%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(222 47% 11%) 0%, hsl(222 47% 8%) 100%);
    --gradient-glow: radial-gradient(ellipse at center, hsl(252 87% 67% / 0.15) 0%, transparent 70%);

    --shadow-glow: 0 0 60px hsl(252 87% 67% / 0.3);
    --shadow-card: 0 4px 24px hsl(222 47% 4% / 0.5);
  }
}

@layer components {
  .glass-card {
    @apply bg-card/80 backdrop-blur-xl border border-border/50 rounded-xl;
    background: var(--gradient-card);
  }

  .gradient-text {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-primary);
  }

  .glow-effect {
    @apply relative;
  }

  .glow-effect::before {
    content: '';
    @apply absolute inset-0 rounded-xl opacity-50 blur-xl -z-10;
    background: var(--gradient-glow);
  }

  .sleep-bar {
    @apply h-2 rounded-full transition-all duration-500 ease-out;
  }

  .stat-value {
    @apply text-4xl font-bold tracking-tight;
  }

  .stat-label {
    @apply text-sm text-muted-foreground uppercase tracking-wider;
  }
}

@layer utilities {
  .animate-in {
    animation: fade-in 0.5s ease-out forwards;
  }

  @keyframes fade-in {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

html {
  color-scheme: dark;
}

body {
  @apply bg-background text-foreground antialiased;
  background-image: var(--gradient-glow);
  background-attachment: fixed;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  @apply bg-background;
}

::-webkit-scrollbar-thumb {
  @apply bg-muted rounded-full;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-muted-foreground/50;
}

