export interface SleepEntry {
  id: string;
  date: string;
  bedtime: string;
  wakeTime: string;
  duration: number; // in hours
  quality: 1 | 2 | 3 | 4 | 5;
  notes?: string;
}

export type SleepQuality = 'excellent' | 'good' | 'fair' | 'poor';

export interface SleepStats {
  averageDuration: number;
  averageQuality: number;
  totalEntries: number;
  weeklyTrend: 'improving' | 'declining' | 'stable';
}

import { SleepEntry, SleepQuality, SleepStats } from '@/types/sleep';

export function calculateDuration(bedtime: string, wakeTime: string): number {
  const [bedHour, bedMin] = bedtime.split(':').map(Number);
  const [wakeHour, wakeMin] = wakeTime.split(':').map(Number);

  let bedMinutes = bedHour * 60 + bedMin;
  let wakeMinutes = wakeHour * 60 + wakeMin;

  // Handle overnight sleep
  if (wakeMinutes <= bedMinutes) {
    wakeMinutes += 24 * 60;
  }

  return (wakeMinutes - bedMinutes) / 60;
}

export function formatDuration(hours: number): string {
  const h = Math.floor(hours);
  const m = Math.round((hours - h) * 60);
  return `${h}h ${m}m`;
}

export function getQualityLabel(quality: number): SleepQuality {
  if (quality >= 4.5) return 'excellent';
  if (quality >= 3.5) return 'good';
  if (quality >= 2.5) return 'fair';
  return 'poor';
}

export function getQualityColor(quality: SleepQuality): string {
  const colors = {
    excellent: 'hsl(var(--sleep-excellent))',
    good: 'hsl(var(--sleep-good))',
    fair: 'hsl(var(--sleep-fair))',
    poor: 'hsl(var(--sleep-poor))',
  };
  return colors[quality];
}

export function calculateStats(entries: SleepEntry[]): SleepStats {
  if (entries.length === 0) {
    return {
      averageDuration: 0,
      averageQuality: 0,
      totalEntries: 0,
      weeklyTrend: 'stable',
    };
  }

  const avgDuration = entries.reduce((sum, e) => sum + e.duration, 0) / entries.length;
  const avgQuality = entries.reduce((sum, e) => sum + e.quality, 0) / entries.length;

  // Calculate trend from last 7 entries
  const recentEntries = entries.slice(-7);
  const olderEntries = entries.slice(-14, -7);

  let trend: 'improving' | 'declining' | 'stable' = 'stable';
  if (recentEntries.length >= 3 && olderEntries.length >= 3) {
    const recentAvg = recentEntries.reduce((s, e) => s + e.quality, 0) / recentEntries.length;
    const olderAvg = olderEntries.reduce((s, e) => s + e.quality, 0) / olderEntries.length;
    if (recentAvg > olderAvg + 0.3) trend = 'improving';
    else if (recentAvg < olderAvg - 0.3) trend = 'declining';
  }

  return {
    averageDuration: avgDuration,
    averageQuality: avgQuality,
    totalEntries: entries.length,
    weeklyTrend: trend,
  };
}

export function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).substr(2);
}

export function formatDate(dateStr: string): string {
  const date = new Date(dateStr);
  return date.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' });
}

import { useState, useEffect, useCallback } from 'react';
import { SleepEntry, SleepStats } from '@/types/sleep';
import { calculateStats, generateId } from '@/utils/sleepUtils';

const STORAGE_KEY = 'dreamtrack-sleep-entries';

// Sample data for demo
const sampleEntries: SleepEntry[] = [
  { id: '1', date: '2025-01-05', bedtime: '22:30', wakeTime: '06:45', duration: 8.25, quality: 4 },
  { id: '2', date: '2025-01-06', bedtime: '23:15', wakeTime: '07:00', duration: 7.75, quality: 3 },
  { id: '3', date: '2025-01-07', bedtime: '22:00', wakeTime: '06:30', duration: 8.5, quality: 5 },
  { id: '4', date: '2025-01-08', bedtime: '23:45', wakeTime: '07:30', duration: 7.75, quality: 3 },
  { id: '5', date: '2025-01-09', bedtime: '22:15', wakeTime: '06:15', duration: 8, quality: 4 },
  { id: '6', date: '2025-01-10', bedtime: '23:00', wakeTime: '07:00', duration: 8, quality: 4 },
  { id: '7', date: '2025-01-11', bedtime: '22:30', wakeTime: '06:30', duration: 8, quality: 5 },
];

export function useSleepData() {
  const [entries, setEntries] = useState<SleepEntry[]>([]);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      try {
        setEntries(JSON.parse(stored));
      } catch {
        setEntries(sampleEntries);
      }
    } else {
      setEntries(sampleEntries);
    }
    setIsLoaded(true);
  }, []);

  useEffect(() => {
    if (isLoaded) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(entries));
    }
  }, [entries, isLoaded]);

  const addEntry = useCallback((entry: Omit<SleepEntry, 'id'>) => {
    const newEntry: SleepEntry = {
      ...entry,
      id: generateId(),
    };
    setEntries(prev => [...prev, newEntry].sort((a, b) =>
      new Date(a.date).getTime() - new Date(b.date).getTime()
    ));
  }, []);

  const removeEntry = useCallback((id: string) => {
    setEntries(prev => prev.filter(e => e.id !== id));
  }, []);

  const stats: SleepStats = calculateStats(entries);
  const recentEntries = entries.slice(-7).reverse();

  return {
    entries,
    recentEntries,
    stats,
    addEntry,
    removeEntry,
    isLoaded,
  };
}

