input.content:
import { Moon, TrendingUp, TrendingDown, Minus, Clock, Star } from 'lucide-react';
import { SleepStats } from '@/types/sleep';
import { formatDuration, getQualityLabel } from '@/utils/sleepUtils';

interface SleepStatsCardsProps {
  stats: SleepStats;
}

export function SleepStatsCards({ stats }: SleepStatsCardsProps) {
  const qualityLabel = getQualityLabel(stats.averageQuality);

  const TrendIcon = stats.weeklyTrend === 'improving'
    ? TrendingUp
    : stats.weeklyTrend === 'declining'
      ? TrendingDown
      : Minus;

  const trendColor = stats.weeklyTrend === 'improving'
    ? 'text-[hsl(var(--sleep-excellent))]'
    : stats.weeklyTrend === 'declining'
      ? 'text-[hsl(var(--sleep-poor))]'
      : 'text-muted-foreground';

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
      <StatCard
        icon={<Moon className="w-5 h-5" />}
        label="Avg Sleep"
        value={formatDuration(stats.averageDuration)}
        sublabel={stats.totalEntries > 0 ? 'per night' : 'no data'}
      />
      <StatCard
        icon={<Star className="w-5 h-5" />}
        label="Quality"
        value={stats.averageQuality > 0 ? stats.averageQuality.toFixed(1) : '-'}
        sublabel={stats.totalEntries > 0 ? qualityLabel : 'no data'}
        qualityClass={`quality-${qualityLabel}`}
      />
      <StatCard
        icon={<Clock className="w-5 h-5" />}
        label="Entries"
        value={stats.totalEntries.toString()}
        sublabel="nights tracked"
      />
      <StatCard
        icon={<TrendIcon className={`w-5 h-5 ${trendColor}`} />}
        label="Trend"
        value={stats.weeklyTrend}
        sublabel="this week"
        valueClass={trendColor}
      />
    </div>
  );
}

interface StatCardProps {
  icon: React.ReactNode;
  label: string;
  value: string;
  sublabel: string;
  qualityClass?: string;
  valueClass?: string;
}

function StatCard({ icon, label, value, sublabel, valueClass }: StatCardProps) {
  return (
    <div className="glass-card p-5 glow-effect animate-in">
      <div className="flex items-center gap-2 text-muted-foreground mb-3">
        {icon}
        <span className="stat-label">{label}</span>
      </div>
      <div className={`stat-value ${valueClass || 'gradient-text'}`}>{value}</div>
      <div className="text-sm text-muted-foreground mt-1 capitalize">{sublabel}</div>
    </div>
  );
}

import { useState } from 'react';
import { Plus, Moon, Sun } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { calculateDuration } from '@/utils/sleepUtils';
import { SleepEntry } from '@/types/sleep';

interface SleepLogFormProps {
  onAdd: (entry: Omit<SleepEntry, 'id'>) => void;
}

export function SleepLogForm({ onAdd }: SleepLogFormProps) {
  const today = new Date().toISOString().split('T')[0];
  const [date, setDate] = useState(today);
  const [bedtime, setBedtime] = useState('22:30');
  const [wakeTime, setWakeTime] = useState('06:30');
  const [quality, setQuality] = useState<1 | 2 | 3 | 4 | 5>(4);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const duration = calculateDuration(bedtime, wakeTime);
    onAdd({ date, bedtime, wakeTime, duration, quality });
    // Reset form
    setDate(today);
    setBedtime('22:30');
    setWakeTime('06:30');
    setQuality(4);
  };

  const duration = calculateDuration(bedtime, wakeTime);
  const hours = Math.floor(duration);
  const minutes = Math.round((duration - hours) * 60);

  return (
    <form onSubmit={handleSubmit} className="glass-card p-6 glow-effect animate-in">
      <h2 className="text-lg font-semibold mb-5 flex items-center gap-2">
        <Plus className="w-5 h-5 text-primary" />
        Log Sleep
      </h2>

      <div className="space-y-5">
        <div>
          <label className="stat-label block mb-2">Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            max={today}
            className="w-full px-4 py-2.5 bg-muted/50 border border-border rounded-lg text-foreground focus:outline-none focus:ring-2 focus:ring-primary/50 transition-all"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="stat-label block mb-2 flex items-center gap-1.5">
              <Moon className="w-3.5 h-3.5" /> Bedtime
            </label>
            <input
              type="time"
              value={bedtime}
              onChange={(e) => setBedtime(e.target.value)}
              className="w-full px-4 py-2.5 bg-muted/50 border border-border rounded-lg text-foreground focus:outline-none focus:ring-2 focus:ring-primary/50 transition-all"
            />
          </div>
          <div>
            <label className="stat-label block mb-2 flex items-center gap-1.5">
